                    category = "Construcción"
                ),
                Job(
                    id = "2",
                    title = "Se busca plomero",
                    description = "Fuga en baño",
                    price = "Presupuesto",
                    type = JobType.DEMAND,
                    providerName = "Usuario",
                    latitude = 19.4340,
                    longitude = -99.1350,
