                    rating = null,
                    category = "Plomería"
                ),
                Job(
                    id = "3",
                    title = "Cuidado de niños",
                    description = "Cuidadora profesional con experiencia",
                    price = "$180/h",
                    type = JobType.OFFER,
                    providerName = "Paola Ramírez",
                    latitude = 19.4310,
                    longitude = -99.1310,
