            viewModel.selectJob(job)
            findNavController().navigate(R.id.jobDetailFragment)
        }
        binding.recyclerViewNearby.apply {
            layoutManager = LinearLayoutManager(requireContext())
            adapter = nearbyAdapter
        }
    }

