        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentJobMapBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        setupMap()
        setupButtons()
        observeJobs()
    }

    private fun setupMap() {
        val context = requireContext()
        Configuration.getInstance().load(context, PreferenceManager.getDefaultSharedPreferences(context))
        Configuration.getInstance().userAgentValue = context.packageName

        binding.mapView.apply {
            setTileSource(TileSourceFactory.MAPNIK)
            setMultiTouchControls(true)
            controller.setZoom(14.0)
            controller.setCenter(GeoPoint(19.4326, -99.1332))
        }

        myLocationOverlay = MyLocationNewOverlay(GpsMyLocationProvider(context), binding.mapView)
        myLocationOverlay.enableMyLocation()
        binding.mapView.overlays.add(myLocationOverlay)

        requestLocationPermission()
    }

    private fun setupButtons() {
        binding.btnMyLocation.setOnClickListener { moveToUserLocation() }
        binding.btnCloseMap.setOnClickListener {
            requireActivity().onBackPressedDispatcher.onBackPressed()
            viewModel.clearSelectedJob()
        }
    }

    private fun observeJobs() {
        viewModel.jobs.observe(viewLifecycleOwner) { addMarkersToMap(it) }
        viewModel.selectedJob.observe(viewLifecycleOwner) { job -> job?.let { focusOnJob(it) } }
    }

    private fun requestLocationPermission() {
        val hasPermission = ContextCompat.checkSelfPermission(
            requireContext(),
            Manifest.permission.ACCESS_FINE_LOCATION
        ) == PackageManager.PERMISSION_GRANTED

        if (hasPermission) {
            enableMyLocation()
        } else {
            requestPermissions(
                arrayOf(
                    Manifest.permission.ACCESS_FINE_LOCATION,
                    Manifest.permission.ACCESS_COARSE_LOCATION
                ),
                LOCATION_PERMISSION_REQUEST
            )
        }
    }

    override fun onRequestPermissionsResult(
        requestCode: Int,
        permissions: Array<out String>,
        grantResults: IntArray
    ) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults)
        if (requestCode == LOCATION_PERMISSION_REQUEST && grantResults.all { it == PackageManager.PERMISSION_GRANTED }) {
            enableMyLocation()
        } else {
            Toast.makeText(requireContext(), "Permiso de ubicación denegado", Toast.LENGTH_SHORT).show()
        }
    }

    private fun enableMyLocation() {
        if (ContextCompat.checkSelfPermission(requireContext(), Manifest.permission.ACCESS_FINE_LOCATION)
            == PackageManager.PERMISSION_GRANTED
        ) {
            myLocationOverlay.enableMyLocation()
            myLocationOverlay.enableFollowLocation()
            moveToUserLocation()
        }
    }

    private fun moveToUserLocation() {
        val context = requireContext()
        if (ContextCompat.checkSelfPermission(context, Manifest.permission.ACCESS_FINE_LOCATION)
            == PackageManager.PERMISSION_GRANTED
        ) {
            val fusedLocationClient = LocationServices.getFusedLocationProviderClient(context)
            fusedLocationClient.lastLocation.addOnSuccessListener { location ->
                location?.let {
                    val userLocation = GeoPoint(it.latitude, it.longitude)
                    binding.mapView.controller.animateTo(userLocation)
                    binding.mapView.controller.setZoom(15.0)
                }
            }
        }
    }

    private fun addMarkersToMap(jobs: List<Job>) {
        markers.forEach { binding.mapView.overlays.remove(it) }
        markers.clear()

        jobs.forEach { job ->
            val marker = Marker(binding.mapView)
            marker.position = GeoPoint(job.latitude, job.longitude)
            marker.title = job.title
            marker.snippet = "${job.providerName} - ${job.price}"
            marker.setOnMarkerClickListener { clickedMarker, _ ->
                viewModel.selectJob(job)
                clickedMarker.showInfoWindow()
                true
            }
            binding.mapView.overlays.add(marker)
            markers.add(marker)
        }
        binding.mapView.invalidate()
    }

    private fun focusOnJob(job: Job) {
        val position = GeoPoint(job.latitude, job.longitude)
        binding.mapView.controller.animateTo(position)
        binding.mapView.controller.setZoom(16.0)
        markers.find { it.position.latitude == job.latitude && it.position.longitude == job.longitude }
            ?.showInfoWindow()
    }

    override fun onResume() {
        super.onResume()
        binding.mapView.onResume()
    }

    override fun onPause() {
        super.onPause()
        binding.mapView.onPause()
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }

    companion object {
        private const val LOCATION_PERMISSION_REQUEST = 100
    }
}

package com.example.chambaya.ui

import android.Manifest
import android.content.pm.PackageManager
import android.os.Bundle
import android.preference.PreferenceManager
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Toast
import androidx.core.content.ContextCompat
import androidx.fragment.app.Fragment
import androidx.fragment.app.activityViewModels
import com.example.chambaya.databinding.FragmentJobMapBinding
import com.example.chambaya.model.Job
import com.google.android.gms.location.LocationServices
import org.osmdroid.config.Configuration
import org.osmdroid.tileprovider.tilesource.TileSourceFactory
import org.osmdroid.util.GeoPoint
import org.osmdroid.views.overlay.Marker
import org.osmdroid.views.overlay.mylocation.GpsMyLocationProvider
import org.osmdroid.views.overlay.mylocation.MyLocationNewOverlay

class JobMapFragment : Fragment() {

    private var _binding: FragmentJobMapBinding? = null
    private val binding get() = _binding!!
    private val viewModel: JobViewModel by activityViewModels()
    private val markers = mutableListOf<Marker>()
    private lateinit var myLocationOverlay: MyLocationNewOverlay

    // Coordenadas de Biobío, Chile (Concepción como referencia)
    private val DEFAULT_LAT = -36.8270
    private val DEFAULT_LON = -73.0497

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentJobMapBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        setupMap()
        setupButtons()
        observeJobs()
    }

    private fun setupMap() {
        val context = requireContext()
        Configuration.getInstance().load(context, PreferenceManager.getDefaultSharedPreferences(context))
        Configuration.getInstance().userAgentValue = context.packageName

        binding.mapView.apply {
            setTileSource(TileSourceFactory.MAPNIK)
            setMultiTouchControls(true)
            controller.setZoom(14.0)
            // Centrar en Biobío, Chile por defecto
            controller.setCenter(GeoPoint(DEFAULT_LAT, DEFAULT_LON))
        }

        myLocationOverlay = MyLocationNewOverlay(GpsMyLocationProvider(context), binding.mapView)
        myLocationOverlay.enableMyLocation()
        binding.mapView.overlays.add(myLocationOverlay)

        requestLocationPermission()
    }

    private fun setupButtons() {
        binding.btnMyLocation.setOnClickListener { moveToUserLocation() }
        binding.btnCloseMap.setOnClickListener {
            requireActivity().onBackPressedDispatcher.onBackPressed()
            viewModel.clearSelectedJob()
        }
    }

    private fun observeJobs() {
        viewModel.jobs.observe(viewLifecycleOwner) { jobs ->
            addMarkersToMap(jobs)
        }

        viewModel.selectedJob.observe(viewLifecycleOwner) { job ->
            job?.let {
                focusOnJob(it)
                // Guardar la ubicación del trabajo seleccionado
                saveJobLocation(it)
            }
        }
    }

    private fun requestLocationPermission() {
        val hasPermission = ContextCompat.checkSelfPermission(
            requireContext(),
            Manifest.permission.ACCESS_FINE_LOCATION
        ) == PackageManager.PERMISSION_GRANTED

        if (hasPermission) {
            enableMyLocation()
        } else {
            requestPermissions(
                arrayOf(
                    Manifest.permission.ACCESS_FINE_LOCATION,
                    Manifest.permission.ACCESS_COARSE_LOCATION
                ),
                LOCATION_PERMISSION_REQUEST
            )
        }
    }

    override fun onRequestPermissionsResult(
        requestCode: Int,
        permissions: Array<out String>,
        grantResults: IntArray
    ) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults)
        if (requestCode == LOCATION_PERMISSION_REQUEST && grantResults.all { it == PackageManager.PERMISSION_GRANTED }) {
            enableMyLocation()
        } else {
            Toast.makeText(requireContext(), "Permiso de ubicación denegado", Toast.LENGTH_SHORT).show()
        }
    }

    private fun enableMyLocation() {
        if (ContextCompat.checkSelfPermission(requireContext(), Manifest.permission.ACCESS_FINE_LOCATION)
            == PackageManager.PERMISSION_GRANTED
        ) {
            myLocationOverlay.enableMyLocation()
            myLocationOverlay.enableFollowLocation()
            moveToUserLocation()
        }
    }

    private fun moveToUserLocation() {
        val context = requireContext()
        if (ContextCompat.checkSelfPermission(context, Manifest.permission.ACCESS_FINE_LOCATION)
            == PackageManager.PERMISSION_GRANTED
        ) {
            val fusedLocationClient = LocationServices.getFusedLocationProviderClient(context)
            fusedLocationClient.lastLocation.addOnSuccessListener { location ->
                location?.let {
                    val userLocation = GeoPoint(it.latitude, it.longitude)
                    binding.mapView.controller.animateTo(userLocation)
                    binding.mapView.controller.setZoom(15.0)
                } ?: run {
                    // Si no hay ubicación, centrar en Biobío
                    binding.mapView.controller.animateTo(GeoPoint(DEFAULT_LAT, DEFAULT_LON))
                    binding.mapView.controller.setZoom(12.0)
                    Toast.makeText(context, "Mostrando región de Biobío, Chile", Toast.LENGTH_SHORT).show()
                }
            }
        }
    }

    private fun addMarkersToMap(jobs: List<Job>) {
        // Limpiar marcadores anteriores
        markers.forEach { binding.mapView.overlays.remove(it) }
        markers.clear()

        jobs.forEach { job ->
            val marker = Marker(binding.mapView)
            marker.position = GeoPoint(job.latitude, job.longitude)
            marker.title = job.title
            marker.snippet = "${job.providerName}\n${job.price}\n${job.distance} km"
            marker.setAnchor(Marker.ANCHOR_CENTER, Marker.ANCHOR_BOTTOM)

            marker.setOnMarkerClickListener { clickedMarker, _ ->
                viewModel.selectJob(job)
                clickedMarker.showInfoWindow()
                true
            }

            binding.mapView.overlays.add(marker)
            markers.add(marker)
        }
        binding.mapView.invalidate()
    }

    private fun focusOnJob(job: Job) {
        val position = GeoPoint(job.latitude, job.longitude)
        binding.mapView.controller.animateTo(position)
        binding.mapView.controller.setZoom(16.0)

        // Encontrar y mostrar el info window del marcador correspondiente
        markers.find { 
            it.position.latitude == job.latitude && 
            it.position.longitude == job.longitude 
        }?.showInfoWindow()
    }

    private fun saveJobLocation(job: Job) {
        // La ubicación ya está guardada en la base de datos Room
        // que actúa como caché local. Para sincronizar con PostgreSQL,
        // se puede llamar al ViewModel para hacer la sincronización
        viewModel.updateJob(job)
    }

    override fun onResume() {
        super.onResume()
        binding.mapView.onResume()
    }

    override fun onPause() {
        super.onPause()
        binding.mapView.onPause()
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }

    companion object {
        private const val LOCATION_PERMISSION_REQUEST = 100
    }
}

package com.example.chambaya.ui

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.example.chambaya.R
import com.example.chambaya.model.Job
import com.example.chambaya.model.JobType

class JobsAdapter(
    private val onJobClick: (Job) -> Unit
) : ListAdapter<Job, JobsAdapter.JobViewHolder>(JobDiffCallback()) {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): JobViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_job, parent, false)
        return JobViewHolder(view, onJobClick)
    }

    override fun onBindViewHolder(holder: JobViewHolder, position: Int) {
        holder.bind(getItem(position))
    }

    class JobViewHolder(
        itemView: View,
        private val onJobClick: (Job) -> Unit
    ) : RecyclerView.ViewHolder(itemView) {

        private val tvTitle: TextView = itemView.findViewById(R.id.tvJobTitle)
        private val tvProvider: TextView = itemView.findViewById(R.id.tvProviderName)
        private val tvPrice: TextView = itemView.findViewById(R.id.tvPrice)
        private val tvType: TextView = itemView.findViewById(R.id.tvJobType)
        private val tvRating: TextView = itemView.findViewById(R.id.tvRating)

        fun bind(job: Job) {
            tvTitle.text = job.title
            tvProvider.text = "${job.providerName} • ${job.distance} km"
            tvPrice.text = job.price
            tvType.text = if (job.type == JobType.OFFER) "Oferta" else "Demanda"

            // Cambiar color de fondo según tipo
            val typeColor = if (job.type == JobType.OFFER) {
                0xFF2196F3.toInt() // Azul
            } else {
                0xFF4CAF50.toInt() // Verde
            }
            tvType.setBackgroundColor(typeColor)

            if (job.rating != null) {
                tvRating.text = "⭐ ${job.rating}"
                tvRating.visibility = View.VISIBLE
            } else {
                tvRating.visibility = View.GONE
            }

            itemView.setOnClickListener {
                onJobClick(job)
            }
        }
    }

    private class JobDiffCallback : DiffUtil.ItemCallback<Job>() {
        override fun areItemsTheSame(oldItem: Job, newItem: Job): Boolean {
            return oldItem.id == newItem.id
        }

        override fun areContentsTheSame(oldItem: Job, newItem: Job): Boolean {
            return oldItem == newItem
        }
    }
}

package com.example.chambaya.ui

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
